import { useSpring, animated } from 'react-spring'

export default function Index(){
  const props = useSpring({
    opacity: 1,
    fontSize: '20px',
    from: {
      opacity: 0,
    }
  })

  return(
    <div>
      <animated.div
        style={props}
      >
        I will fade in
      </animated.div>
    </div>
  )
}
