/** @jsx jsx */
import { jsx, css } from '@emotion/core'
import styled from '@emotion/styled'
import { animated, useSpring, interpolate, config } from 'react-spring'
import { size } from 'polished'
import CenterLayout from '../components/CenterLayout'
import { useRef } from 'react'
import transform from '../libs/transform'

export default function Index() {
  const [spring, setSpring] = useSpring(() => ({
    config: config.gentle,
    rotateX: 0,
    rotateY: 0
  }))

  const cardInfo = useRef({
    pointXInCard: 0,
    pointYInCard: 0
  }).current

  const handleMouseOver = (ev)=>{
    const { target, clientX, clientY } = ev
    const { top,left,width,height } = target.getBoundingClientRect()
    setSpring({
      rotateX: transform(clientY, [clientY-top,clientY-top+height], [-30,30]),
      rotateY: transform(clientX, [clientX-left, clientX-left+width], [-30,30]),
      scale: 1.2
    })
  }

  return (
    <CenterLayout>
      <AnimatedCard
        onMouseOver={handleMouseOver}
        onMouseOut={() => setSpring({
          rotateX: 0,
          rotateY: 0,
          scale: 1
        })}
        style={{ transform: `perspective(1000px) rotateX(${spring.rotateX}) rotateY(${spring.rotateY}) scale(${spring.scale})` }}
      >
        <img
          src='https://images.pexels.com/photos/3558597/pexels-photo-3558597.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500'
          alt=''
        />
      </AnimatedCard>
    </CenterLayout>
  )
}

const AnimatedCard = styled(animated.div)`
  ${size(400)};
  border-radius: 10px;
  overflow: hidden;
  box-shadow: 0 10px 40px rgba(0, 0, 0, 0.2);
  cursor: pointer;
  /* transform: perspective(600px) rotateX(-30deg) rotateY(-30deg); */
  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`
