/** @jsx jsx */
import { jsx, css } from '@emotion/core'
import styled from '@emotion/styled'
import { animated, useSpring, interpolate } from 'react-spring'
import { size } from 'polished'
import CenterLayout from '../components/CenterLayout'

export default function Index() {
  const [spring, setSpring] = useSpring(() => ({
    scale: 1
  }))

  return (
    <CenterLayout>
      <AnimatedCard
        onMouseOver={() => setSpring({ scale: 1.2 })}
        onMouseOut={() => setSpring({ scale: 1 })}
        style={{ transform: spring.scale.interpolate(s=> `scale(${s})`) }}
      >
        <img
          src='https://images.pexels.com/photos/3558597/pexels-photo-3558597.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500'
          alt=''
        />
      </AnimatedCard>
    </CenterLayout>
  )
}

const AnimatedCard = styled(animated.div)`
  ${size(400)};
  border-radius: 10px;
  overflow: hidden;
  box-shadow: 0 10px 40px rgba(0, 0, 0, 0.2);
  cursor: pointer;
  /* transform: perspective(600px) rotateX(-30deg) rotateY(-30deg); */
  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`
